import React from "react";
import {Product} from "./Product";
import {useEffect} from "@types/react";
import axios from "axios";

export function ProductList({products = [], onAddClick, onRemoveClick}) {
    useEffect(() => {
        axios.get('http://localhost:8080/api/v1/products')
            .then(v => setProducts(v.data));
    }, []);
  return (
    <React.Fragment>
    <h5 className="flex-grow-0"><b>상품 목록</b></h5>
    <div className="row">
        <div className="col">
            <div className="col btn btn-sm btn-outline-dark" style={{fontSize:"13px"}}>
                전체
            </div>
        </div>
        {/*<div className="col">*/}
        {/*    <a className="col btn btn-sm btn-outline-dark" style="font-size: 13px"></a>*/}
        {/*</div>*/}
    </div>
      <ul className="list-group products">
        {products.map(v =>
          <li key={v.productId} className="list-group-item d-flex mt-3">
            <Product {...v} onAddClick={onAddClick} onRemoveClick={onRemoveClick}/>
          </li>
        )}
      </ul>
    </React.Fragment>
  )
}
