import React from "react";
import {Product} from "./Product";
import {useEffect, useState} from "react";
import axios from "axios";
import Order from "../pages/order";
import {CategoryUpdate} from "./CategoryUpdate";

export function ProductList({products = [], onAddClick, onRemoveClick, onUpdateClick} ) {

    const [categories, setCategory] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:8080/api/v1/category')
            .then(v => setCategory(v.data));
    }, []);







  return (
    <React.Fragment>
    <h5 className="flex-grow-0"><b>상품 목록</b></h5>
    <div className="row">
        <div className="col">
            <div className="col btn btn-sm btn-outline-dark" style={{fontSize:"13px"}}>
                전체
            </div>
        </div>
        <div className="col">
            {categories.map(v =>
                <CategoryUpdate category = {v}/>
            )}
        </div>
    </div>
      <ul className="list-group products">
        {products.map(v =>
          <li key={v.productId} className="list-group-item d-flex mt-3">
            <Product {...v} onAddClick={onAddClick} onRemoveClick={onRemoveClick}/>
          </li>
        )}
      </ul>
    </React.Fragment>
  )
}
