import React from "@types/react";

export function Product(props) {
    const id = props.id;

    const productName = props.productName;
    const category = props.category;
    const price = props.price;

    const handleAddBtnClicked = e => {
      console.log(id, "clicked")
    };

    return (
        <>
            <div className="col-2">
                <img className="img-fluid" src="https://i.imgur.com/HKOFQYa.jpeg" alt=""/>
            </div>
            <div className="col">
                <div className="row text-muted">{category}</div>
                <div className="row">{productName}</div>
            </div>
            <div className="col text-center price">{price}원</div>
            <div className="col text-end action">
                <a onClick={handleAddBtnClicked} className="btn btn-small btn-outline-dark" href="my-app/src/components/Product">추가</a>
            </div>
        </>
    )
}
